CREATE TABLE TripParticipants (
      participant_id INTEGER PRIMARY KEY AUTOINCREMENT,
      trip_id INTEGER NOT NULL,
      client_id INTEGER NOT NULL,
      role TEXT DEFAULT 'traveler',
      created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
      notes TEXT,
