      id INTEGER PRIMARY KEY AUTOINCREMENT,
      trip_id INTEGER NOT NULL,
      day_id INTEGER,
      activity_type TEXT,
      title TEXT,
      start_time TEXT,
      end_time TEXT,
      location TEXT,
      cost REAL,
