INSERT INTO PracticalInfo VALUES(1,1,'Weather','Dublin (September)','Temperature Range: 51-64°F (11-18°C)\nPrecipitation: 7-10 rainy days typical\nSeasonal Considerations: Autumn weather can be changeable; layers recommended\nDaylight Hours: Approximately 12-13 hours of daylight','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(2,1,'Weather','London & Stoneleigh (September)','Temperature Range: 54-66°F (12-19°C)\nPrecipitation: 8-10 rainy days typical\nSeasonal Considerations: Similar to Dublin; can be pleasant but prepare for rain\nDaylight Hours: Approximately 12-13 hours of daylight','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(3,1,'Packing','Clothing Items','• Waterproof jacket or rain shell\n• Lightweight sweaters or cardigans for layering\n• Long-sleeve shirts and t-shirts\n• Comfortable walking pants/jeans\n• One dressier outfit for nice dinners\n• Comfortable walking shoes (waterproof if possible)\n• Light scarf (versatile for variable temperatures)','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(4,1,'Packing','Travel Essentials','• UK/Ireland electrical adapter (Type G, 3-pin)\n• Portable phone charger/power bank\n• Small daypack for daily excursions\n• Reusable water bottle\n• Small umbrella\n• Medications in original containers\n• Copy of passport and travel documents','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(5,1,'Packing','Equestrian/Professional Items','• Any specific equipment needed for farrier work portion (check with work contacts)\n• Casual professional attire for work meetings\n• Appropriate footwear for equestrian environments','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(6,1,'Travel Requirements','Visa Requirements','• US citizens do not need a visa for tourist visits to Ireland or the UK for stays under 90 days\n• Valid passport required with at least six months validity beyond your return date\n• At least two blank passport pages required','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(7,1,'Travel Requirements','Vaccinations','• No special vaccinations required for travel to Ireland and the UK\n• Routine vaccinations (MMR, Tdap, etc.) should be up-to-date\n• COVID-19 requirements: Check current regulations closer to departure date','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(8,1,'Travel Requirements','Travel Insurance','Highly recommended for all international travel. Should include:\n• Trip cancellation/interruption coverage\n• Medical coverage\n• Emergency evacuation\n• Baggage loss/delay\n• Rental car coverage','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(9,1,'Travel Requirements','Currency','• Ireland: Euro (€)\n• United Kingdom: British Pound (£)\n• Credit cards widely accepted; inform your bank of travel plans\n• Recommend carrying small amount of local currency for small purchases\n• ATMs readily available in both countries','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(10,1,'Emergency','Emergency Contacts','• Ireland Emergency Services: 112 or 999\n• UK Emergency Services: 999 or 112\n• US Embassy in Ireland: +353 1 668 8777\n• US Embassy in UK: +44 20 7499 9000','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(11,1,'Emergency','Nearest Hospitals','• Dublin: St. Vincent''s University Hospital, Elm Park, Dublin 4\n  Tel: +353 1 221 4000\n• London: St. Thomas'' Hospital, Westminster Bridge Road\n  Tel: +44 20 7188 7188\n• Warwickshire: University Hospital Coventry, Clifford Bridge Road\n  Tel: +44 24 7696 4000','2025-03-30 22:12:53','2025-03-30 22:12:53');
INSERT INTO PracticalInfo VALUES(12,3,'Emergency Contacts','Emergency Contact Information','Emergency: 911\nBreckenridge Medical Center: (970) 453-1010\nBreckenridge Police (non-emergency): (970) 453-2941\nBeaver Run Resort: (970) 547-8500','2025-04-05 14:30:56','2025-04-05 14:30:56');
INSERT INTO PracticalInfo VALUES(13,3,'Packing','Ski Gear','Ski jackets & pants\nBase layers & mid layers\nSki socks (multiple pairs)\nGloves/mittens & hand warmers\nNeck gaiter or balaclava\nHelmet & goggles','2025-04-05 14:30:56','2025-04-05 14:30:56');
INSERT INTO PracticalInfo VALUES(14,3,'Packing','Winter Items','Winter boots with good traction\nCasual winter wear for evenings\nHat and gloves for non-ski activities\nSwimwear (for hot tubs)','2025-04-05 14:30:56','2025-04-05 14:30:56');
INSERT INTO PracticalInfo VALUES(15,3,'Altitude','High Altitude Precautions','Breckenridge sits at 9,600 feet elevation\nStay hydrated! Drink more water than you think you need\nLimit alcohol consumption, especially on your first day\nConsider starting with half-day activities to acclimate\nSymptoms of altitude sickness include headache, nausea, and fatigue\nIf symptoms persist, seek medical attention','2025-04-05 14:30:56','2025-04-05 14:30:56');
INSERT INTO PracticalInfo VALUES(16,4,'Weather','April Weather Conditions',replace('Average high: 11°C (52°F)\nAverage low: 4°C (39°F)\nRainfall likely\nChangeable conditions\n\nRequired Gear:\n- Waterproof jackets\n- Walking shoes\n- Layers for temperature changes\n- Camera protection','\n',char(10)),'2025-04-16 18:17:50','2025-04-16 18:17:50');
INSERT INTO PracticalInfo VALUES(17,4,'Emergency','Emergency Contacts',replace('American Airlines: +1-800-433-7300\nBritish Airways: +44-844-493-0787\nAvis Car Rental (Inverness Airport): +44-344-544-6017\nMuthu Newton Hotel: +44-1667-453144','\n',char(10)),'2025-04-16 18:17:56','2025-04-16 18:17:56');
INSERT INTO PracticalInfo VALUES(18,4,'Accessibility','Accessibility Notes',replace('Wheelchair assistance requested at all airports\nMary needs rest during long walks\nAccessible paths prioritized in itinerary\nElevator access at Inverness apartment\nGround floor rooms at Angus B&B','\n',char(10)),'2025-04-16 18:19:27','2025-04-16 18:19:27');
INSERT INTO PracticalInfo VALUES(19,4,'Accessibility','Isle of Skye Accessibility Notes',replace('- Dunvegan Castle & Gardens: Well-maintained paths suitable for Mary\n- Kilt Rock & Mealt Falls Viewpoint: Easily accessible from car park\n- Staffin Dinosaur Museum: Accessible facility with nearby dinosaur footprints at beach\n- Skye Museum of Island Life: Relatively accessible traditional buildings\n- Portree Harbor: Gentle walking area with colorful buildings and sea views\n- Sligachan Viewpoint: Beautiful mountain landscapes visible from car park\n- Talisker Bay: Sunset viewpoint (partial accessibility)\n- Old Man of Storr: Viewable from car park (hiking optional for Neil and Kim)\n- Dunbeg Broch: 10-minute uphill walk for Neil and Kim while Mary rests in car','\n',char(10)),'2025-04-16 19:08:23','2025-04-16 19:08:23');
