INSERT INTO db_functions VALUES('initialize_session',replace('CREATE FUNCTION initialize_session(p_session_id TEXT, p_session_name TEXT, p_trip_id INTEGER, p_client_id INTEGER, p_details TEXT)\n    RETURNS TEXT\n    BEGIN\n        -- Insert into session management\n        INSERT INTO SessionManagement (\n            session_id, \n            session_name, \n            primary_trip_id, \n            primary_client_id, \n            session_summary\n        )\n        VALUES (\n            p_session_id,\n            p_session_name,\n            p_trip_id,\n            p_client_id,\n            p_details\n        );\n        \n        -- Log the activity\n        INSERT INTO ActivityLog (\n            session_id,\n            trip_id,\n            client_id,\n            activity_type,\n            details\n        )\n        VALUES (\n            p_session_id,\n            p_trip_id,\n            p_client_id,\n            ''SessionStart'',\n            p_details\n        );\n        \n        RETURN p_session_id;\n    END;','\n',char(10)));
INSERT INTO db_functions VALUES('get_recent_activities',replace('CREATE FUNCTION get_recent_activities(days_ago INTEGER DEFAULT 7)\n    RETURNS TABLE (\n        session_id TEXT,\n        last_activity TEXT,\n        trip_name TEXT,\n        client_name TEXT,\n        activity_summary TEXT\n    )\n    BEGIN\n        RETURN SELECT \n            a.session_id,\n            MAX(a.activity_timestamp) as last_activity,\n            t.trip_name,\n            CASE \n                WHEN c.first_name IS NOT NULL THEN c.first_name || '' '' || c.last_name\n                ELSE NULL \n            END as client_name,\n            GROUP_CONCAT(DISTINCT a.activity_type, '', '') as activity_summary\n        FROM ActivityLog a\n        LEFT JOIN Trips t ON a.trip_id = t.trip_id\n        LEFT JOIN Clients c ON a.client_id = c.client_id\n        WHERE a.activity_timestamp > datetime(''now'', ''-'' || days_ago || '' days'')\n        GROUP BY a.session_id\n        ORDER BY last_activity DESC\n        LIMIT 5;\n    END;','\n',char(10)));
