INSERT INTO DocumentTemplates VALUES(4,'Standard Trip Guide','Trip Guide',replace('<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="UTF-8">\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <title>{{trip_name}} - Travel Guide</title>\n    <style>\n        body { font-family: Arial, sans-serif; line-height: 1.6; max-width: 800px; margin: 0 auto; padding: 20px; }\n        h1, h2, h3, h4 { color: #2c3e50; }\n        .header { text-align: center; margin-bottom: 30px; }\n        .contact-info { text-align: center; font-size: 0.9em; margin-bottom: 30px; border: 1px solid #ddd; padding: 10px; background-color: #f8f9fa; }\n        .section { margin-bottom: 30px; }\n        .checklist li { margin-bottom: 10px; }\n        .day { margin-bottom: 40px; border-bottom: 1px solid #eee; padding-bottom: 20px; }\n        .activity { margin-left: 20px; margin-bottom: 20px; }\n        .time { font-weight: bold; color: #3498db; }\n        .location { font-style: italic; }\n        .accommodation { background-color: #e8f4f8; padding: 10px; margin-bottom: 15px; border-left: 4px solid #3498db; }\n        .transportation { background-color: #fff3e0; padding: 10px; margin-bottom: 15px; border-left: 4px solid #f39c12; }\n        .practical-info { background-color: #f8f9fa; padding: 15px; margin: 30px 0; border-radius: 5px; }\n        .emergency { background-color: #fee; padding: 15px; margin: 30px 0; border-left: 4px solid #e74c3c; }\n        .weather { background-color: #f1f8e9; padding: 10px; margin-bottom: 15px; }\n        .important { color: #e74c3c; font-weight: bold; }\n        .highlight { background-color: #fffde7; padding: 5px; }\n        .footer { margin-top: 50px; font-size: 0.8em; text-align: center; color: #7f8c8d; }\n        .hidden-gem { background-color: #e0f7fa; padding: 10px; margin: 15px 0; border-left: 4px solid #00bcd4; }\n        .tip { background-color: #f3e5f5; padding: 10px; margin: 15px 0; border-left: 4px solid #9c27b0; }\n        @media print {\n            body { font-size: 12pt; }\n            .no-print { display: none; }\n            .day { page-break-after: always; }\n        }\n    </style>\n</head>\n<body>\n    <div class="header">\n        <h1>{{trip_name}}</h1>\n        <p>{{start_date}} to {{end_date}} ({{duration}} days)</p>\n    </div>\n\n    <div class="contact-info">\n        <p><strong>Your Travel Specialist: Kim Henderson</strong><br>\n        Phone: 251-508-6921<br>\n        Email: kim.henderson@cruiseplanners.com<br>\n        <span class="important">In case of emergency during your trip, please call: +1-251-508-6921</span></p>\n    </div>\n\n    <div class="section">\n        <h2>Before You Go</h2>\n        \n        <div class="checklist">\n            <h3>Pre-Trip Checklist</h3>\n            <ul>\n                {{block:pre_trip_checklist}}\n            </ul>\n        </div>\n        \n        <div class="checklist">\n            <h3>Night-Before Checklist</h3>\n            <ul>\n                {{block:night_before_checklist}}\n            </ul>\n        </div>\n        \n        <div class="weather">\n            <h3>Weather Forecast</h3>\n            <p>{{weather_forecast}}</p>\n            \n            <h4>Packing Recommendations</h4>\n            <ul>\n                {{block:packing_recommendations:{{destination_country}}}}\n            </ul>\n        </div>\n    </div>\n\n    <div class="emergency">\n        <h3>Emergency Information for {{destination_country}}</h3>\n        {{block:emergency_info:{{destination_country}}}}\n    </div>\n\n    <div class="practical-info">\n        <h3>Practical Information</h3>\n        \n        <h4>Local Currency</h4>\n        <p>{{local_currency}} ({{currency_code}})</p>\n        <p>Exchange Rate: approximately {{exchange_rate}} per USD</p>\n        \n        <h4>Tipping Customs</h4>\n        <p>{{block:tipping_customs:{{destination_country}}}}</p>\n        \n        <h4>Local Transportation</h4>\n        <p>{{block:transportation_guide:{{destination_country}}}}</p>\n        \n        {{if:is_driving_trip}}\n        <h4>Driving Information</h4>\n        <p>{{block:driving_tips:{{destination_country}}}}</p>\n        {{endif}}\n    </div>\n\n    <h2>Your Day-by-Day Itinerary</h2>\n    \n    {{for:each_day}}\n    <div class="day">\n        <h3>Day {{day_number}}: {{day_name}} ({{date}})</h3>\n        <p>{{day_description}}</p>\n        \n        {{if:has_accommodation}}\n        <div class="accommodation">\n            <h4>Tonight''s Accommodation</h4>\n            <p><strong>{{accommodation_name}}</strong><br>\n            {{accommodation_address}}<br>\n            Phone: {{accommodation_phone}}</p>\n            \n            <p>Check-in: {{check_in_time}}<br>\n            Room Type: {{room_type}}<br>\n            Confirmation #: {{confirmation_number}}</p>\n            \n            {{if:has_accommodation_notes}}\n            <p><em>Notes: {{accommodation_notes}}</em></p>\n            {{endif}}\n        </div>\n        {{endif}}\n        \n        {{for:each_activity}}\n        <div class="activity">\n            <h4>{{activity_title}}</h4>\n            <p class="time">{{activity_time}}</p>\n            <p>{{activity_description}}</p>\n            <p class="location">Location: {{activity_location}}</p>\n            \n            {{if:has_activity_notes}}\n            <p><em>Notes: {{activity_notes}}</em></p>\n            {{endif}}\n            \n            {{if:is_hidden_gem}}\n            <div class="hidden-gem">\n                <p><strong>🌟 Hidden Gem:</strong> {{hidden_gem_description}}</p>\n            </div>\n            {{endif}}\n        </div>\n        {{endfor}}\n        \n        {{if:has_transportation}}\n        <div class="transportation">\n            <h4>Transportation Details</h4>\n            <p><strong>{{transportation_type}}</strong>: {{transportation_details}}</p>\n            <p>From: {{departure_location}} at {{departure_time}}<br>\n            To: {{arrival_location}} at {{arrival_time}}</p>\n            <p>Confirmation #: {{transportation_confirmation}}</p>\n            \n            {{if:has_transportation_notes}}\n            <p><em>Notes: {{transportation_notes}}</em></p>\n            {{endif}}\n        </div>\n        {{endif}}\n        \n        {{if:has_dining_recommendations}}\n        <div class="tip">\n            <h4>Dining Recommendations</h4>\n            <ul>\n                {{for:each_dining_recommendation}}\n                <li><strong>{{dining_name}}</strong> - {{dining_description}}<br>\n                <span class="location">{{dining_address}}</span></li>\n                {{endfor}}\n            </ul>\n        </div>\n        {{endif}}\n    </div>\n    {{endfor}}\n    \n    <div class="footer">\n        <p>This travel guide was prepared exclusively for {{client_names}} by SOMO Travel Specialists.<br>\n        Last updated: {{created_at}}</p>\n    </div>\n</body>\n</html>','\n',char(10)),0,'2025-05-09 15:46:20','Comprehensive trip guide template with practical information and detailed itinerary');
INSERT INTO DocumentTemplates VALUES(5,'Enhanced Trip Guide with Collapsible Sections','Trip Guide',replace('<!DOCTYPE html>\n<html>\n<head>\n    <meta charset="UTF-8">\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <title>{{trip_name}} - Travel Guide</title>\n    <style>\n        body { \n            font-family: Arial, sans-serif; \n            line-height: 1.6; \n            max-width: 800px; \n            margin: 0 auto; \n            padding: 20px;\n            color: #333;\n        }\n        h1, h2, h3, h4 { color: #2c3e50; }\n        .header { \n            text-align: center; \n            margin-bottom: 30px; \n            background-color: #f8f9fa;\n            padding: 20px;\n            border-radius: 8px;\n        }\n        .contact-info { \n            text-align: center; \n            font-size: 0.9em; \n            margin-bottom: 30px; \n            border: 1px solid #ddd; \n            padding: 15px; \n            background-color: #f8f9fa;\n            border-radius: 8px;\n        }\n        .section { margin-bottom: 30px; }\n        .checklist li { margin-bottom: 10px; }\n        .day { \n            margin-bottom: 20px; \n            border: 1px solid #eee; \n            border-radius: 8px;\n            overflow: hidden;\n        }\n        .day-header {\n            background-color: #f5f5f5;\n            padding: 10px 15px;\n            cursor: pointer;\n            border-bottom: 1px solid #eee;\n        }\n        .day-content {\n            padding: 15px;\n        }\n        .activity { \n            margin-left: 20px; \n            margin-bottom: 20px; \n            padding: 10px;\n            border-left: 3px solid #3498db;\n            background-color: #f9f9f9;\n        }\n        .time { font-weight: bold; color: #3498db; }\n        .location { font-style: italic; }\n        .accommodation { \n            background-color: #e8f4f8; \n            padding: 15px; \n            margin-bottom: 15px; \n            border-left: 4px solid #3498db;\n            border-radius: 4px;\n        }\n        .transportation { \n            background-color: #fff3e0; \n            padding: 15px; \n            margin-bottom: 15px; \n            border-left: 4px solid #f39c12;\n            border-radius: 4px;\n        }\n        .practical-info { \n            background-color: #f8f9fa; \n            padding: 15px; \n            margin: 30px 0; \n            border-radius: 5px;\n        }\n        .emergency { \n            background-color: #fee; \n            padding: 15px; \n            margin: 30px 0; \n            border-left: 4px solid #e74c3c;\n            border-radius: 4px;\n        }\n        .weather { \n            background-color: #f1f8e9; \n            padding: 15px; \n            margin-bottom: 15px;\n            border-radius: 4px;\n        }\n        .important { color: #e74c3c; font-weight: bold; }\n        .highlight { background-color: #fffde7; padding: 5px; }\n        .footer { \n            margin-top: 50px; \n            font-size: 0.8em; \n            text-align: center; \n            color: #7f8c8d;\n            padding: 20px;\n            background-color: #f8f9fa;\n            border-radius: 8px;\n        }\n        .hidden-gem { \n            background-color: #e0f7fa; \n            padding: 15px; \n            margin: 15px 0; \n            border-left: 4px solid #00bcd4;\n            border-radius: 4px;\n        }\n        .tip { \n            background-color: #f3e5f5; \n            padding: 15px; \n            margin: 15px 0; \n            border-left: 4px solid #9c27b0;\n            border-radius: 4px;\n        }\n        \n        /* Styles for collapsible sections */\n        details {\n            margin-bottom: 20px;\n            border: 1px solid #ddd;\n            border-radius: 8px;\n            overflow: hidden;\n        }\n        \n        summary {\n            padding: 12px 15px;\n            background-color: #f5f5f5;\n            cursor: pointer;\n            font-weight: bold;\n            border-bottom: 1px solid #ddd;\n        }\n        \n        details[open] summary {\n            border-bottom: 1px solid #ddd;\n        }\n        \n        details > div {\n            padding: 15px;\n        }\n        \n        /* Style for the table of contents */\n        .toc {\n            background-color: #f8f9fa;\n            padding: 15px;\n            border-radius: 8px;\n            margin-bottom: 30px;\n        }\n        \n        .toc ul {\n            list-style-type: none;\n            padding-left: 15px;\n        }\n        \n        .toc li {\n            margin-bottom: 8px;\n        }\n        \n        .toc a {\n            text-decoration: none;\n            color: #3498db;\n        }\n        \n        .toc a:hover {\n            text-decoration: underline;\n        }\n        \n        @media print {\n            body { font-size: 12pt; }\n            .no-print { display: none; }\n            details { page-break-inside: avoid; }\n            details > summary { font-weight: bold; }\n            details[open] > summary { display: none; }\n            details > div { display: block !important; }\n        }\n    </style>\n</head>\n<body>\n    <div class="header">\n        <h1>{{trip_name}}</h1>\n        <p>{{start_date}} to {{end_date}} ({{duration}} days)</p>\n    </div>\n\n    <div class="contact-info">\n        <p><strong>Your Travel Specialist: Kim Henderson</strong><br>\n        Phone: 251-508-6921<br>\n        Email: kim.henderson@cruiseplanners.com<br>\n        <span class="important">In case of emergency during your trip, please call: +1-251-508-6921</span></p>\n    </div>\n    \n    <!-- Table of Contents -->\n    <div class="toc">\n        <h2>Quick Navigation</h2>\n        <ul>\n            <li><a href="#before-you-go">Before You Go</a></li>\n            <li><a href="#emergency">Emergency Information</a></li>\n            <li><a href="#practical-info">Practical Information</a></li>\n            <li><a href="#itinerary">Day-by-Day Itinerary</a></li>\n        </ul>\n    </div>\n\n    <div class="section" id="before-you-go">\n        <h2>Before You Go</h2>\n        \n        <details>\n            <summary>Pre-Trip Checklist</summary>\n            <div class="checklist">\n                <ul>\n                    {{block:pre_trip_checklist}}\n                </ul>\n            </div>\n        </details>\n        \n        <details>\n            <summary>Night-Before Checklist</summary>\n            <div class="checklist">\n                <ul>\n                    {{block:night_before_checklist}}\n                </ul>\n            </div>\n        </details>\n        \n        <details>\n            <summary>Weather & Packing Information</summary>\n            <div class="weather">\n                <h3>Weather Forecast</h3>\n                <p>{{weather_forecast}}</p>\n                \n                <h4>Packing Recommendations</h4>\n                <ul>\n                    {{block:packing_recommendations:{{destination_country}}}}\n                </ul>\n            </div>\n        </details>\n    </div>\n\n    <details id="emergency">\n        <summary><h2 style="display: inline; margin: 0;">Emergency Information</h2></summary>\n        <div class="emergency">\n            <h3>Emergency Information for {{destination_country}}</h3>\n            {{block:emergency_info:{{destination_country}}}}\n        </div>\n    </details>\n\n    <details id="practical-info">\n        <summary><h2 style="display: inline; margin: 0;">Practical Information</h2></summary>\n        <div class="practical-info">\n            <details>\n                <summary>Currency & Money</summary>\n                <div>\n                    <h4>Local Currency</h4>\n                    <p>{{local_currency}} ({{currency_code}})</p>\n                    <p>Exchange Rate: approximately {{exchange_rate}} per USD</p>\n                    <p><strong>Money Tips:</strong></p>\n                    <ul>\n                        <li>ATMs are widely available in cities and towns</li>\n                        <li>Notify your bank before traveling to avoid card blocks</li>\n                        <li>Most establishments accept credit cards, but some small rural businesses may be cash-only</li>\n                        <li>Keep some cash on hand for small purchases and rural areas</li>\n                    </ul>\n                </div>\n            </details>\n            \n            <details>\n                <summary>Tipping Guidelines</summary>\n                <div>\n                    {{block:tipping_customs:{{destination_country}}}}\n                </div>\n            </details>\n            \n            <details>\n                <summary>Transportation Options</summary>\n                <div>\n                    {{block:transportation_guide:{{destination_country}}}}\n                </div>\n            </details>\n            \n            <details>\n                <summary>Driving Information</summary>\n                <div>\n                    {{if:is_driving_trip}}\n                    {{block:driving_tips:{{destination_country}}}}\n                    {{endif}}\n                </div>\n            </details>\n            \n            <details>\n                <summary>Communication & Internet</summary>\n                <div>\n                    <h4>Phone & Internet</h4>\n                    <ul>\n                        <li>Country code for {{destination_country}} is {{country_code}}</li>\n                        <li>Wi-Fi is widely available in accommodations, cafes, and restaurants</li>\n                        <li>Consider a local SIM card for data (available at major airports)</li>\n                        <li>Most tourist areas have good coverage, but expect spotty service in remote rural areas</li>\n                    </ul>\n                </div>\n            </details>\n        </div>\n    </details>\n\n    <h2 id="itinerary">Your Day-by-Day Itinerary</h2>\n    \n    {{for:each_day}}\n    <details class="day">\n        <summary class="day-header">\n            <strong>Day {{day_number}}:</strong> {{day_name}} ({{date}})\n        </summary>\n        <div class="day-content">\n            <p>{{day_description}}</p>\n            \n            {{if:has_accommodation}}\n            <div class="accommodation">\n                <h4>Tonight''s Accommodation</h4>\n                <p><strong>{{accommodation_name}}</strong><br>\n                {{accommodation_address}}<br>\n                Phone: {{accommodation_phone}}</p>\n                \n                <p>Check-in: {{check_in_time}}<br>\n                Room Type: {{room_type}}<br>\n                Confirmation #: {{confirmation_number}}</p>\n                \n                {{if:has_accommodation_notes}}\n                <p><em>Notes: {{accommodation_notes}}</em></p>\n                {{endif}}\n            </div>\n            {{endif}}\n            \n            {{if:has_transportation}}\n            <div class="transportation">\n                <h4>Transportation Details</h4>\n                <p><strong>{{transportation_type}}</strong>: {{transportation_details}}</p>\n                <p>From: {{departure_location}} at {{departure_time}}<br>\n                To: {{arrival_location}} at {{arrival_time}}</p>\n                <p>Confirmation #: {{transportation_confirmation}}</p>\n                \n                {{if:has_transportation_notes}}\n                <p><em>Notes: {{transportation_notes}}</em></p>\n                {{endif}}\n            </div>\n            {{endif}}\n            \n            {{for:each_activity}}\n            <details class="activity">\n                <summary><strong>{{activity_time}}:</strong> {{activity_title}}</summary>\n                <div>\n                    <p>{{activity_description}}</p>\n                    <p class="location">Location: {{activity_location}}</p>\n                    \n                    {{if:has_activity_notes}}\n                    <p><em>Notes: {{activity_notes}}</em></p>\n                    {{endif}}\n                    \n                    {{if:is_hidden_gem}}\n                    <div class="hidden-gem">\n                        <p><strong>🌟 Hidden Gem:</strong> {{hidden_gem_description}}</p>\n                    </div>\n                    {{endif}}\n                </div>\n            </details>\n            {{endfor}}\n            \n            {{if:has_dining_recommendations}}\n            <div class="tip">\n                <h4>Dining Recommendations</h4>\n                <ul>\n                    {{for:each_dining_recommendation}}\n                    <li><strong>{{dining_name}}</strong> - {{dining_description}}<br>\n                    <span class="location">{{dining_address}}</span></li>\n                    {{endfor}}\n                </ul>\n            </div>\n            {{endif}}\n        </div>\n    </details>\n    {{endfor}}\n    \n    <div class="footer">\n        <p>This travel guide was prepared exclusively for {{client_names}} by SOMO Travel Specialists.<br>\n        Last updated: {{created_at}}</p>\n    </div>\n</body>\n</html>','\n',char(10)),1,'2025-05-09 17:56:53','Enhanced template with collapsible sections for better mobile and user experience');
