INSERT INTO DatabaseProcedures VALUES(1,'ReclassifyEntity','Reclassify an entity from one type to another based on verification',replace('BEGIN\n        -- Parameters: entity_id, original_type, new_type, verification_id\n        -- This is pseudo-code as SQLite doesn''t support stored procedures\n        -- Claude will use this as a reference for the SQL to execute\n        \n        -- 1. If reclassifying accommodation to dining\n        --    - Move data from Accommodations to DiningRecommendations\n        --    - Update TripActivities references\n        --    - Mark verification as complete\n        \n        -- 2. If reclassifying dining to accommodation\n        --    - Move data from DiningRecommendations to Accommodations\n        --    - Update TripActivities references\n        --    - Mark verification as complete\n        \n        -- 3. If reclassifying to attraction\n        --    - Create entry in appropriate table\n        --    - Update TripActivities references\n        --    - Mark verification as complete\n        \n        -- 4. Log the reclassification in ActivityLog\n        --    - Use session_id from VerificationLog\n        --    - Set activity_type to ''EntityReclassification''\n        --    - Include details about the change\n    END;','\n',char(10)),'2025-05-09 14:47:50');
INSERT INTO DatabaseProcedures VALUES(2,'migrate_existing_trips_to_products','Migrates existing accommodations and transportation data to trip_products table with 3-tier pricing',replace('\n  -- Migrate accommodations\n  INSERT INTO trip_products (\n    trip_id, product_type, product_name, destination_city, destination_country,\n    travel_date, pricing_tiers, standard_price, currency,\n    product_details, availability_status, recommendation_tier,\n    search_text, created_at, updated_at\n  )\n  SELECT \n    a.trip_id,\n    ''hotel'' as product_type,\n    a.accommodation_name as product_name,\n    a.city as destination_city,\n    a.country as destination_country,\n    a.check_in_date as travel_date,\n    json_object(\n      ''budget'', json_object(\n        ''price'', COALESCE(a.price_per_night * 0.8, a.total_cost * 0.8, 100),\n        ''currency'', COALESCE(a.currency, ''USD''),\n        ''features'', json_array(''Basic Room'', ''WiFi'')\n      ),\n      ''standard'', json_object(\n        ''price'', COALESCE(a.price_per_night, a.total_cost, 150),\n        ''currency'', COALESCE(a.currency, ''USD''),\n        ''features'', json_array(''Standard Room'', ''WiFi'', ''Breakfast'')\n      ),\n      ''premium'', json_object(\n        ''price'', COALESCE(a.price_per_night * 1.5, a.total_cost * 1.5, 250),\n        ''currency'', COALESCE(a.currency, ''USD''),\n        ''features'', json_array(''Deluxe Room'', ''WiFi'', ''Breakfast'', ''Premium Amenities'')\n      )\n    ) as pricing_tiers,\n    COALESCE(a.price_per_night, a.total_cost, 150) as standard_price,\n    COALESCE(a.currency, ''USD'') as currency,\n    json_object(\n      ''address'', a.address,\n      ''phone'', a.phone,\n      ''website'', a.website,\n      ''rating'', a.rating,\n      ''amenities'', a.amenities,\n      ''room_type'', a.room_type,\n      ''confirmation_number'', a.confirmation_number\n    ) as product_details,\n    CASE WHEN a.status = ''confirmed'' THEN ''available'' ELSE ''unknown'' END as availability_status,\n    ''recommended'' as recommendation_tier,\n    a.accommodation_name || '' '' || COALESCE(a.city, '''') || '' '' || COALESCE(a.country, '''') || '' hotel accommodation'' as search_text,\n    COALESCE(a.created_at, datetime(''now'')) as created_at,\n    COALESCE(a.updated_at, datetime(''now'')) as updated_at\n  FROM Accommodations a\n  WHERE NOT EXISTS (\n    SELECT 1 FROM trip_products tp \n    WHERE tp.trip_id = a.trip_id \n    AND tp.product_type = ''hotel'' \n    AND tp.product_name = a.accommodation_name\n  );\n  \n  -- Migrate transportation\n  INSERT INTO trip_products (\n    trip_id, product_type, product_name, destination_city, destination_country,\n    travel_date, pricing_tiers, standard_price, currency,\n    product_details, availability_status, recommendation_tier,\n    search_text, created_at, updated_at\n  )\n  SELECT \n    t.trip_id,\n    CASE \n      WHEN t.transport_type LIKE ''%flight%'' OR t.transport_type LIKE ''%air%'' THEN ''flight''\n      WHEN t.transport_type LIKE ''%train%'' THEN ''transport''\n      WHEN t.transport_type LIKE ''%bus%'' THEN ''transport''\n      WHEN t.transport_type LIKE ''%car%'' THEN ''transport''\n      ELSE ''transport''\n    END as product_type,\n    COALESCE(t.provider_name, t.transport_type) || '' '' || t.departure_location || '' to '' || t.arrival_location as product_name,\n    t.arrival_location as destination_city,\n    NULL as destination_country,\n    COALESCE(t.departure_datetime, t.departure_time) as travel_date,\n    json_object(\n      ''budget'', json_object(\n        ''price'', COALESCE(t.cost * 0.7, 50),\n        ''currency'', COALESCE(t.currency, ''USD''),\n        ''features'', json_array(''Economy'', ''Basic Service'')\n      ),\n      ''standard'', json_object(\n        ''price'', COALESCE(t.cost, 100),\n        ''currency'', COALESCE(t.currency, ''USD''),\n        ''features'', json_array(''Standard'', ''Regular Service'')\n      ),\n      ''premium'', json_object(\n        ''price'', COALESCE(t.cost * 1.8, 200),\n        ''currency'', COALESCE(t.currency, ''USD''),\n        ''features'', json_array(''Premium'', ''Enhanced Service'', ''Priority Boarding'')\n      )\n    ) as pricing_tiers,\n    COALESCE(t.cost, 100) as standard_price,\n    COALESCE(t.currency, ''USD'') as currency,\n    json_object(\n      ''transport_type'', t.transport_type,\n      ''provider_name'', t.provider_name,\n      ''departure_location'', t.departure_location,\n      ''departure_time'', t.departure_time,\n      ''arrival_location'', t.arrival_location,\n      ''arrival_time'', t.arrival_time,\n      ''duration'', t.duration,\n      ''confirmation_number'', t.confirmation_number,\n      ''reference_number'', t.reference_number\n    ) as product_details,\n    CASE WHEN t.status = ''confirmed'' THEN ''available'' ELSE ''unknown'' END as availability_status,\n    ''recommended'' as recommendation_tier,\n    t.transport_type || '' '' || t.departure_location || '' '' || t.arrival_location || '' transportation'' as search_text,\n    COALESCE(t.created_at, datetime(''now'')) as created_at,\n    COALESCE(t.updated_at, datetime(''now'')) as updated_at\n  FROM Transportation t\n  WHERE NOT EXISTS (\n    SELECT 1 FROM trip_products tp \n    WHERE tp.trip_id = t.trip_id \n    AND tp.product_type IN (''flight'', ''transport'')\n    AND tp.product_name LIKE ''%'' || t.departure_location || ''%'' || t.arrival_location || ''%''\n  );\n  ','\n',char(10)),'2025-08-21 06:50:21');
