INSERT INTO ContentBlocks VALUES(1,'Pre-Trip Checklist','pre_trip_checklist',NULL,replace('<li>✅ Verify passport validity (must be valid for at least 6 months beyond your return date)</li>\n<li>✅ Check visa requirements for all destinations</li>\n<li>✅ Purchase travel insurance</li>\n<li>✅ Make copies of important documents (passport, insurance, itinerary)</li>\n<li>✅ Inform your bank and credit card companies of your travel dates</li>\n<li>✅ Arrange for pet/plant care and mail collection</li>\n<li>✅ Check the weather forecast and pack accordingly</li>\n<li>✅ Exchange some currency or verify ATM access at your destination</li>\n<li>✅ Download offline maps and translation apps</li>\n<li>✅ Confirm all reservations (flights, accommodations, activities)</li>\n<li>✅ Pack essential medications and a basic first-aid kit</li>\n<li>✅ Check roaming capabilities or purchase an international data plan</li>','\n',char(10)),'Standard pre-trip checklist for all destinations','2025-05-09 17:50:32','2025-05-09 17:50:32');
INSERT INTO ContentBlocks VALUES(2,'Night-Before Checklist','night_before_checklist',NULL,replace('<li>✅ Complete online check-in for your flight (if not already done)</li>\n<li>✅ Charge all electronic devices</li>\n<li>✅ Pack chargers and adapters in your carry-on</li>\n<li>✅ Set multiple alarms if you have an early departure</li>\n<li>✅ Prepare transportation to the airport</li>\n<li>✅ Check flight status for any delays or cancellations</li>\n<li>✅ Place liquids in regulation size containers for carry-on</li>\n<li>✅ Weigh your luggage to ensure it meets airline requirements</li>\n<li>✅ Secure your home (lock windows/doors, adjust thermostat)</li>\n<li>✅ Print or save electronic copies of all travel documents</li>\n<li>✅ Prepare any snacks for the journey</li>','\n',char(10)),'Standard night-before-departure checklist','2025-05-09 17:50:39','2025-05-09 17:50:39');
INSERT INTO ContentBlocks VALUES(3,'Ireland Emergency Information','emergency_info','IE',replace('<h4>Emergency Numbers</h4>\n<p><strong>General Emergency (Police, Fire, Ambulance):</strong> 112 or 999</p>\n<p><strong>US Embassy in Dublin:</strong> +353 1 668 8777<br>\n42 Elgin Road, Ballsbridge, Dublin 4</p>\n\n<h4>Medical Assistance</h4>\n<p>Public hospitals in Ireland provide emergency care. Show your passport and travel insurance details.</p>\n<p><strong>Hospital Recommendations:</strong></p>\n<ul>\n<li><strong>Dublin:</strong> St. Vincent''s University Hospital, Elm Park, Dublin 4 - Tel: +353 1 221 4000</li>\n<li><strong>Cork:</strong> Cork University Hospital, Wilton, Cork - Tel: +353 21 492 2000</li>\n<li><strong>Galway:</strong> University Hospital Galway, Newcastle Road - Tel: +353 91 524 222</li>\n</ul>','\n',char(10)),'Emergency contact information for Ireland','2025-05-09 17:50:47','2025-05-09 17:50:47');
INSERT INTO ContentBlocks VALUES(4,'Ireland Driving Tips','driving_tips','IE',replace('<p><strong>Important:</strong> In Ireland, they drive on the LEFT side of the road.</p>\n\n<ul>\n<li>Your US driver''s license is valid in Ireland for up to 12 months as a tourist.</li>\n<li>Manual transmission cars are more common. Request an automatic if needed.</li>\n<li>Roads can be very narrow, especially in rural areas.</li>\n<li>Speed limits: 120 km/h (75 mph) on motorways, 100 km/h (62 mph) on national roads, 80 km/h (50 mph) on regional roads, 50 km/h (31 mph) in urban areas.</li>\n<li>Allow extra time for journeys - Google Maps estimates are often optimistic for rural Irish roads.</li>\n<li>Be cautious of roundabouts - remember to yield to traffic from the right.</li>\n<li>Many rural roads are unmarked and may have stone walls directly alongside them with no shoulder.</li>\n<li>Watch for sheep or other livestock on roads in rural areas.</li>\n<li>Fuel stations are less common in rural areas - keep your tank at least half-full.</li>\n</ul>\n\n<p><strong>Parking:</strong> Major towns have paid parking. Look for blue "P" signs. In Dublin, be cautious of private clamping companies.</p>','\n',char(10)),'Driving tips specific to Ireland','2025-05-09 17:50:57','2025-05-09 17:50:57');
INSERT INTO ContentBlocks VALUES(5,'Ireland Transportation Guide','transportation_guide','IE',replace('<h4>Public Transport Options</h4>\n\n<p><strong>Dublin</strong></p>\n<ul>\n<li><strong>Dublin Bus:</strong> Extensive network throughout the city. Exact change required or use a Leap Card.</li>\n<li><strong>Luas:</strong> Tram system with Red and Green lines. Purchase tickets at machines before boarding.</li>\n<li><strong>DART:</strong> Coastal train service. Great for visiting coastal towns like Howth and Bray.</li>\n</ul>\n\n<p><strong>Intercity Travel</strong></p>\n<ul>\n<li><strong>Irish Rail (Iarnród Éireann):</strong> Connects major cities and towns. Book in advance for better prices.</li>\n<li><strong>Bus Éireann:</strong> National bus service. More comprehensive than rail for smaller towns.</li>\n<li><strong>Private Coaches:</strong> Companies like GoBus and Aircoach offer express services between major destinations.</li>\n</ul>\n\n<p><strong>Leap Card:</strong> If staying in Dublin for several days, consider purchasing a Leap Card for use on buses, trams, and trains. Available at Dublin Airport and many convenience stores.</p>\n\n<p><strong>Taxis:</strong> Widely available in cities. Look for the official taxi sign on the roof. All licensed taxis use meters. Ride-sharing apps like Free Now also operate in major cities.</p>','\n',char(10)),'Transportation guide for Ireland','2025-05-09 17:51:06','2025-05-09 17:51:06');
INSERT INTO ContentBlocks VALUES(6,'Ireland Tipping Customs','tipping_customs','IE',replace('<p>Tipping is appreciated but not as mandatory as in the US. Here are the common practices:</p>\n\n<ul>\n<li><strong>Restaurants:</strong> 10-15% for good service. Check if service charge is already included.</li>\n<li><strong>Pubs:</strong> Not customary to tip for drinks at the bar, but you can round up or say "keep the change."</li>\n<li><strong>Taxis:</strong> Typically round up to the nearest euro or add 10% for longer journeys.</li>\n<li><strong>Hotels:</strong> €1-2 per bag for porters; €2-3 per day for housekeeping.</li>\n<li><strong>Tour Guides:</strong> €5-10 per person for a half-day tour, €10-20 for a full day tour.</li>\n</ul>','\n',char(10)),'Tipping customs in Ireland','2025-05-09 17:51:13','2025-05-09 17:51:13');
INSERT INTO ContentBlocks VALUES(7,'Ireland Packing Recommendations','packing_recommendations','IE',replace('<li>🧥 <strong>Waterproof jacket/rain coat</strong> - Essential regardless of season</li>\n<li>👕 <strong>Layers</strong> - Weather can change quickly; bring items you can layer</li>\n<li>👞 <strong>Comfortable waterproof walking shoes</strong> - For exploring cities and natural areas</li>\n<li>🔌 <strong>Power adapter</strong> - Ireland uses Type G plugs (same as UK)</li>\n<li>👜 <strong>Small backpack/day bag</strong> - For carrying essentials during day trips</li>\n<li>🧢 <strong>Hat</strong> - Sun protection in summer; warm hat in other seasons</li>\n<li>🧣 <strong>Scarf</strong> - Useful year-round</li>\n<li>☂️ <strong>Compact umbrella</strong> - For unexpected showers</li>\n<li>🧴 <strong>Sunscreen</strong> - Even on cloudy days</li>\n<li>📱 <strong>Phone with offline maps</strong> - Useful for navigation in rural areas</li>\n<li>🚗 <strong>If driving:</strong> Printed maps as backup for rural areas with poor signal</li>','\n',char(10)),'Packing recommendations for Ireland','2025-05-09 17:51:22','2025-05-09 17:51:22');
