CREATE VIEW client_search_view AS
SELECT 
    c.client_id,
    c.email,
    c.full_name,
    c.preferences,
    c.total_trips,
    c.total_spent,
    c.last_trip_date,
    c.created_at,
    c.updated_at,
    -- Extract contact details using string functions (D1 compatible)
    CASE 
        WHEN c.contact_info LIKE '%"phone"%' AND c.contact_info NOT LIKE '%"phone":null%' THEN
            substr(c.contact_info, 
                instr(c.contact_info, '"phone":"') + 9,
                instr(substr(c.contact_info, instr(c.contact_info, '"phone":"') + 9), '"') - 1
            )
        ELSE NULL
    END as phone,
    CASE 
        WHEN c.contact_info LIKE '%"country"%' AND c.contact_info NOT LIKE '%"country":null%' THEN
            substr(c.contact_info, 
                instr(c.contact_info, '"country":"') + 11,
                instr(substr(c.contact_info, instr(c.contact_info, '"country":"') + 11), '"') - 1
            )
        ELSE NULL
    END as country,
    -- Calculate active trip count by searching for client_id in trips_v2
    (
        SELECT COUNT(*)
        FROM trips_v2 t
        WHERE t.clients LIKE '%"client_id":' || c.client_id || '%'
    ) as active_trip_count,
    -- Find latest trip start date for this client
    (
        SELECT MAX(t.start_date)
        FROM trips_v2 t
        WHERE t.clients LIKE '%"client_id":' || c.client_id || '%'
    ) as latest_trip_start_date,
    -- Calculate total spent on active trips
    (
        SELECT SUM(t.total_cost)
        FROM trips_v2 t
        WHERE t.clients LIKE '%"client_id":' || c.client_id || '%'
        AND t.total_cost IS NOT NULL
    ) as calculated_total_spent,
    -- Full contact info for advanced queries
    c.contact_info as contact_info_json,
    -- Search text
